mapper.update:
UPDATE building
SET bookmark_count = IFNULL(bookmark_count, 0) + 1
WHERE building_id = #{buildingId}

UPDATE building
SET bookmark_count = IFNULL(bookmark_count, 0) - 1
WHERE building_id = #{buildingId}

UPDATE bookmark
SET price = #{price}
WHERE bookmark_id = #{bookmarkId}

mapper.delete:
DELETE FROM bookmark
WHERE user_id = #{userId}
  AND building_id = #{buildingId}

mapper.select:
SELECT user_id
FROM bookmark
WHERE building_id = #{buildingId}

SELECT building_id
FROM bookmark
WHERE member_id = #{memberId}
