mapper.select:
SELECT chat_message_id, chat_room_id, building_id, member_id AS senderId, complex_id,  message, created_at, is_read
FROM chat_message
WHERE chat_room_id = #{chatRoomId}


    AND chat_message_id < #{lastMessageId}


ORDER BY created_at DESC, chat_message_id DESC
LIMIT #{limit}

SELECT COUNT(*) FROM chat_message
WHERE chat_room_id = #{chatRoomId}
  AND member_id != #{userId}
  AND is_read = false

SELECT * FROM chat_message WHERE chat_room_id = #{chatRoomId}
ORDER BY created_at DESC, chat_message_id DESC
LIMIT 1
