mapper.update:
UPDATE notification
SET is_read = TRUE
WHERE member_id = #{userId}
  AND notification_id = #{notificationId}

mapper.delete:
DELETE FROM notification
WHERE member_id = #{userId}
  AND notification_id = #{notificationId}

mapper.insert:
INSERT INTO notification (
    member_id, message, is_read, type, created_at,
    sale_type, price, address, rank
) VALUES (
             #{userId}, #{message}, false, #{type},
             NOW(), #{saleType}, #{price}, #{address}, #{rank}
         )

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM notification
    WHERE member_id = #{memberId}
      AND type = #{type}
      AND price = #{price}
      AND created_at >= CURDATE()
      AND building_id = #{buildingId}
)
