mapper.select:
SELECT notification_id AS notificationId, member_id AS memberId, message, is_read AS isRead, `type`, created_at AS createdAt, sale_type AS saleType, price, address, `rank`, building_id AS buildingId FROM notification WHERE member_id = #{memberId}
AND `type` = #{type}
ORDER BY created_at DESC

mapper.update:
UPDATE notification
SET is_read = true
WHERE member_id = #{memberId}
  AND is_read = false
  AND notification_id = #{notificationId}

mapper.delete:
DELETE FROM notification
WHERE member_id = #{memberId}
  AND notification_id = #{notificationId}

mapper.insert:
INSERT INTO notification (
    member_id, building_id, message, is_read, `type`, created_at,
    sale_type, price, address, `rank`
) VALUES (
             #{memberId}, #{buildingId}, #{message}, false, #{type},
             NOW(), #{saleType}, #{price}, #{address}, #{rank}
         )

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM notification
    WHERE member_id = #{memberId}
      AND `type` = #{type}
      AND price = #{price}
      AND building_id = #{buildingId}
)
